$(document).ready(function(){var e=document.querySelector(".main__nav__toggle"),s=document.querySelector(".site__list"),o=document.querySelector(".only-desk"),t=document.querySelector(".modal"),c=document.querySelector(".podl"),l=document.querySelector(".modal-close");e.addEventListener("click",function(){e.classList.toggle("open"),e.classList.contains("open")?(s.classList.remove("hide"),s.classList.add("show")):(s.classList.add("hide"),s.classList.remove("show"))}),o.addEventListener("click",function(e){e.preventDefault(),t.classList.add("modal-show"),c.classList.add("podl-1")}),c.addEventListener("click",function(e){e.preventDefault(),t.classList.contains("modal-show")&&(t.classList.remove("modal-show"),c.classList.remove("podl-1"))}),l.addEventListener("click",function(e){e.preventDefault(),t.classList.remove("modal-show"),c.classList.remove("podl-1")}),window.addEventListener("keydown",function(e){27===e.keyCode&&t.classList.contains("modal-show")&&(t.classList.remove("modal-show"),c.classList.remove("podl-1"))})});